        project.setRepositoryUrl("https://github.com/example/ecommerce");
        project.setTechStacks(List.of("Spring Boot", "React", "MySQL"));

        reqDTO.setProjects(List.of(project));

        // trainings
        ResumeRequest.SaveDTO.TrainingDTO training = new ResumeRequest.SaveDTO.TrainingDTO();
        training.setStartDate(LocalDate.of(2024, 1, 1));
        training.setEndDate(LocalDate.of(2024, 6, 30));
        training.setIsOngoing(false);
        training.setCourseName("백엔드 마스터과정");
        training.setInstitutionName("코딩 부트캠프");
        training.setDescription("실무 중심의 백엔드 개발 교육");
        training.setTechStacks(List.of("Spring", "JPA", "MySQL"));

        reqDTO.setTrainings(List.of(training));

        // etcs
        ResumeRequest.SaveDTO.EtcDTO etc = new ResumeRequest.SaveDTO.EtcDTO();
        etc.setStartDate(LocalDate.of(2023, 5, 1));
        etc.setEndDate(LocalDate.of(2023, 6, 1));
